
public class TestPatterns {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        for(int i=1;i<=7;i++)
        {
        	for(int j=i;j>1;j--)
        	{
        		System.out.print(" ");
        	}
        	for(int k=7;k>=i;k--)
        	{
        		System.out.print("*");
        	}
        	
        	System.out.println();
        	
        	
        	
        }
        
	}

}
